import axios from 'axios';

// const apiUrl = "http://localhost:5240"
axios.defaults.baseURL = "http://localhost:5240"
axios.interceptors.response.use(function (response) {
  return response;
}, function (error) {
  // Any status codes that falls outside the range of 2xx cause this function to trigger
  // Do something with response error
  return Promise.reject(error);
});
export default {
  getTasks: async () => {
    // const result = await axios.get(`${apiUrl}/items`)

    const result = await axios.get(`/items`)
    console.log(result);    
    return result.data;
  },

  addTask: async(name)=>{
    // console.log('addTask', name)
    const result=await axios.put(`/addItems`,{name:name})
    return result.data;
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    isComplete === true ? isComplete=1:isComplete=0;
    const result=await axios.post(`/updateItems/${id}`)
    return result.data;
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result=await axios.delete(`/DeleteItems/${id}`)
    return result.data
  }
  };
